        public static int DesiredWidth = 0;
        public static float WindowScale = 1.0f;

        public static void InitAndRun()
        {

            Directory.SetCurrentDirectory(Environment.GetEnvironmentVariable("appdata"));

            Process[] processes = Process.GetProcesses();

            ToolkitOptions options = new ToolkitOptions();

            options.ApplicationName = "WonderfulScreensaver";
            // options.Logger = new ConsoleLogger();
            Toolkit.Init(options);

            OpenGLGraphicsApiHints contextOptions = new OpenGLGraphicsApiHints()
            {

                Version = new Version(3, 1),
                Profile = OpenGLProfile.Core,
                // DebugFlag = false,
                DepthBits = ContextDepthBits.Depth24,
                StencilBits = ContextStencilBits.Stencil8

            };

            Window = Toolkit.Window.Create(contextOptions);
            OpenGLContextHandle openglContext = Toolkit.OpenGL.CreateFromWindow(Window);

            WindowComponent component = new WindowComponent();

            Toolkit.OpenGL.SetCurrentContext(openglContext);
            GLLoader.LoadBindings(Toolkit.OpenGL.GetBindingsContext(openglContext));

            EventQueue.EventRaised += EventRaised;

            Toolkit.Window.SetTitle(Window, "screensaver");

            Toolkit.Window.SetBorderStyle(Window, WindowBorderStyle.Borderless);
            Toolkit.Window.SetMode(Window, WindowMode.Normal);

            if (IsInPreview)
            {

                Console.WriteLine("doing necessary things to parent to window..");

                IntPtr thisHandle = component.GetHWND(Window);

                SetParent(thisHandle, PreviewHandle);
                SetWindowLong(thisHandle, -16, new IntPtr(GetWindowLong(thisHandle, -16) | 0x40000000));

                GetClientRect(PreviewHandle, out Rectangle rect);

                GlobalValues.WIDTH = rect.Width;
                GlobalValues.HEIGHT = rect.Height;

                Console.WriteLine($"{GlobalValues.WIDTH}, {GlobalValues.HEIGHT}");

                Toolkit.Window.SetSize(Window, (rect.Width, rect.Height));
                Toolkit.Window.SetBorderStyle(Window, WindowBorderStyle.Borderless);
                Toolkit.Window.GetFramebufferSize(Window, out Vector2i size);
                GL.Viewport(0, 0, size.X, size.Y);

                DisplayHandle displayHandle = Toolkit.Display.Open(0);
                Toolkit.Display.GetResolution(displayHandle, out int width, out int height);

                DesiredWidth = width;

                Sprite.SpriteCamera.UpdateProjectionMatrix();

            } else
            {

                int displayCount = Toolkit.Display.GetDisplayCount();
                int width = 0;
                int height = 0;

                for (int i = 0; i < displayCount; i++)
                {

                    DisplayHandle h = Toolkit.Display.Open(i);
                    Toolkit.Display.GetResolution(h, out int w, out int he);

                    width += w;
                    height = Math.Max(height, he);

                }

                Toolkit.Window.SetMode(Window, WindowMode.WindowedFullscreen);
                Toolkit.Window.SetSize(Window, (width, height));

                IsFullscreen = true;

                Toolkit.Window.SetCursor(Window, null);

            }

            Vector2? mousePos = null;

            Position = StartingPosition;
            StartingVelocityDirection *= (float)(2.0 * Math.PI);

            Velocity = (Vector2) new Vector2d(Math.Cos(StartingVelocityDirection), Math.Sin(StartingVelocityDirection));
            Velocity *= PixelsPerMeter;

            GL.Enable(EnableCap.Blend);
            GL.BlendFunc(BlendingFactor.SrcAlpha, BlendingFactor.OneMinusSrcAlpha);

            GlobalValues.PreviousTime = Stopwatch.GetTimestamp();
           
            List<Vector2> points = new List<Vector2>();

            List<Sprite> sprites = new List<Sprite>();

            double t = 0.0;
            Random r = new Random();

            Texture tex = new Texture(snowParticleBytes, 64, 64);
           

            if (DesiredWidth != 0)
            {

                WindowScale = GlobalValues.WIDTH / DesiredWidth;

            }

            while (IsRunning)
            {

                GlobalValues.Time += GlobalValues.DeltaTime;

                GlobalValues.CurrentTime = Stopwatch.GetTimestamp();
                GlobalValues.DeltaTime = (GlobalValues.CurrentTime - GlobalValues.PreviousTime) / Stopwatch.Frequency;
                GlobalValues.PreviousTime = GlobalValues.CurrentTime;

                GL.Clear(ClearBufferMask.DepthBufferBit | ClearBufferMask.ColorBufferBit);

                GL.ClearColor(Color4.Black);

                Toolkit.Window.ProcessEvents(false);

                Velocity += new Vector2(0, 9.8f * PixelsPerMeter) * (float) GlobalValues.DeltaTime;

                Position += (Velocity * PixelsPerMeter) * (float)GlobalValues.DeltaTime;

                double angle = 0.0;

                if (!IsFirstFrame && ShouldClose) if (MouseDelta != Vector2.Zero) break;

                if (IsFirstFrame) IsFirstFrame = false;

                if (t > 0.001)
                {

                    t -= 0.1;

                }
                t += GlobalValues.DeltaTime;

                Vector2 p = (r.Next(-(int)GlobalValues.HEIGHT - 150, (int)GlobalValues.WIDTH + 150 + 1 + (int)GlobalValues.HEIGHT), WindowScale * (-6 - r.Next(0, 150)));
                sprites.Add(new Sprite(tex, (50, 50), p, (0.5f, 0.5f), Vector3.Zero, Lerp(0.1f, 0.25f, (float)(r.NextDouble()))));

                for (int i = 0; i < sprites.Count; i++)
                {

                    if (sprites[i].Position.Y > GlobalValues.HEIGHT + 40)
                    {

                        sprites[i].SpriteShader.Dispose();
                        sprites.RemoveAt(i);
                        continue;

                    }

                    sprites[i].Draw();

                    sprites[i].Velocity.Y += (float)((50.0) * GlobalValues.DeltaTime);

                    sprites[i].Velocity.X -= (float) ((50.0) * GlobalValues.DeltaTime);
                    sprites[i].Velocity.X += (float) (((50.0 * Math.Sin(sprites[i].Position.Y / 50.0))) * GlobalValues.DeltaTime);
                  
                    sprites[i].Position += ((sprites[i].Velocity * WindowScale) * (float)GlobalValues.DeltaTime);
                    sprites[i].Scale -= ((0.01f * WindowScale) * (float)GlobalValues.DeltaTime);

                }

                Toolkit.OpenGL.SwapBuffers(openglContext);

            }

            if (!IsInPreview)
            {

                Toolkit.Window.Destroy(Window);

            }

        }

        public static void Destroy()
        {

            Toolkit.Window.Destroy(Window);

        }

        public static float Lerp(float a, float b, float t)
        {

            return a + t * (b - a);

        }

