            // Sprite.Draw(null, tileSize, (Vector2)x4y4, origin, Vector3.Zero, 1);

            x1y1 = (cubeRadius * Math.Cos(time), cubeRadius * Math.Sin(time));
            x2y2 = (cubeRadius * Math.Cos(time + (Math.PI / 2.0)), cubeRadius * Math.Sin(time + (Math.PI / 2.0)));
            x3y3 = (cubeRadius * Math.Cos(time + ((3.0 * Math.PI) / 2.0)), cubeRadius * Math.Sin(time + ((3.0 * Math.PI) / 2.0)));
            x4y4 = (cubeRadius * Math.Cos(time + Math.PI), cubeRadius * Math.Sin(time + Math.PI));

            x1y1.Y *= Math.Cos(time);
            x2y2.Y *= Math.Cos(time);
            x3y3.Y *= Math.Cos(time);
            x4y4.Y *= Math.Cos(time);

            x1y1.Y += (Math.Sin(time + Math.PI) * (cubeSideLength / 2.0));
            x2y2.Y += (Math.Sin(time + Math.PI) * (cubeSideLength / 2.0));
            x3y3.Y += (Math.Sin(time + Math.PI) * (cubeSideLength / 2.0));
            x4y4.Y += (Math.Sin(time + Math.PI) * (cubeSideLength / 2.0));

            x1y1 += position;
            x2y2 += position;
            x3y3 += position;
            x4y4 += position;

            // Sprite.Draw(null, tileSize, (Vector2)x1y1, origin, Vector3.Zero, 1);
            // Sprite.Draw(null, tileSize, (Vector2)x2y2, origin, Vector3.Zero, 1);
            // Sprite.Draw(null, tileSize, (Vector2)x3y3, origin, Vector3.Zero, 1);
            // Sprite.Draw(null, tileSize, (Vector2)x4y4, origin, Vector3.Zero, 1);

        }

        private static void EventRaised(PalHandle? handle, PlatformEventType type, EventArgs args)
        {

            if (!IsInPreview)
            {

                if (args is MouseMoveEventArgs e)
                {

                    CurrentMousePosition = e.ClientPosition;
                    MouseDelta = CurrentMousePosition - PreviousMousePosition;
                    PreviousMousePosition = CurrentMousePosition;

                }

                if (args is MouseButtonDownEventArgs)
                {

                    if (ShouldClose) IsRunning = false;

                }

                if (args is KeyDownEventArgs)
                {

                    if (ShouldClose) IsRunning = false;

                }

            }

            if (args is CloseEventArgs closeEvent)
            {

                IsRunning = false;

            }

            if (args is WindowResizeEventArgs windowResize)
            {

                Console.WriteLine(windowResize.NewClientSize);

                GlobalValues.WIDTH = windowResize.NewClientSize.X;
                GlobalValues.HEIGHT = windowResize.NewClientSize.Y;

                Toolkit.Window.GetFramebufferSize(windowResize.Window, out Vector2i size);
                GL.Viewport(0, 0, size.X, size.Y);

                Sprite.SpriteCamera.UpdateProjectionMatrix();

            }
            
        }

    }
